import React, { useState } from "react";
import axios from "axios";
import "./UploadForm.css";

const UploadForm = () => {
  const [imageFile, setImageFile] = useState(null);
  const [fileName, setFileName] = useState("Please upload image file");

  const imageSelectHandler = (event) => {
    const imageFileInfo = event.target.files[0];
    setImageFile(imageFileInfo);
    setFileName(imageFileInfo.name);
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("imageTest", imageFile);
    try {
      const res = await axios.post("/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("success");
    } catch (err) {
      alert("fail");
    }
  };

  return (
    <>
      <form onSubmit={onSubmit}>
        <div className="file-dropper">
          {fileName}
          <input id="image" type="file" onChange={imageSelectHandler} />
        </div>
        <button
          type="submit"
          style={{ width: "100%", height: 40, borderRadius: 3 }}
        >
          Submit
        </button>
      </form>
    </>
  );
};

export default UploadForm;
